local positions = {Position:new(posx()-1, posy()-1, posz()), Position:new(posx()-1, posy(), posz()), Position:new(posx()-1, posy()+1, posz()), Position:new(posx(), posy(), posz()), Position:new(posx(), posy()+1, posz()), Position:new(posx()+1, posy()-1, posz()), Position:new(posx()+1, posy(), posz()),Position:new(posx()+1, posy()+1, posz())} local idtodestroy = {2524,2358, 2359, 2360, 2361}
function findtrough() local toolposition = getitempositionfromcontainers(3308) for _, pos in ipairs(positions) do for _, id in ipairs(idtodestroy) do local tile = getitemsontile(pos) local objectindex = finditemindex(tile, id)
while objectindex ~= -1 do usetwoobjects(toolposition, 3308, toolposition.z, pos, id, objectindex) tile = getitemsontile(pos) objectindex = finditemindex(tile, id) waitping() end end end end
auto(2000) findtrough()
